package co.istad.springwebmvc.controller;import co.istad.springwebmvc.model.Product;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.media.Content;import io.swagger.v3.oas.annotations.media.Schema;import io.swagger.v3.oas.annotations.responses.ApiResponse;import io.swagger.v3.oas.annotations.responses.ApiResponses;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.awt.print.Book;import java.util.HashMap;import java.util.List;import java.util.Map;@RestController@RequestMapping("/api/v1/categories")public class CategoryController {    @Operation(summary = "Get all categories")    @ApiResponses(value = {            @ApiResponse(responseCode = "200", description = "Found the book",                    content = { @Content(mediaType = "application/json",                            schema = @Schema(implementation =   Product.class)) }),            @ApiResponse(responseCode = "400", description = "Invalid id supplied",                    content = @Content),            @ApiResponse(responseCode = "404", description = "Book not found",                    content = @Content) })    @GetMapping    Map<String, Object> FindCategories(){        Map<String, Object> data = new HashMap<>();        data.put("message", "Categories has been found");        data.put("data", List.of("Education", "Entertainment"));        return data;    }}