package co.istad.springwebmvc.dto;import jakarta.validation.constraints.Max;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Positive;import lombok.*;@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class ProductCreateRequest {    @NotBlank    private String name;    @Positive    @NotNull    private Double price;    @Positive    @NotNull    @Max(100)    private Integer qty;}